import React from "react";

function UpdatePost(){
    return(
        <>
            <div className="a">
            <h1>Sửa bài viết</h1>
            </div>

            <input name="idPost" id="idPost" value="{data.id}" type="hidden"/>
            <textarea className="text" rows = "5" cols = "80" name="content" id="content" type="text" placeholder="Nhập nội dung cần sửa"></textarea>
            <button className="button">Sửa bài viết</button>
        </>
    )
}

export default UpdatePost;
