import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import './Home.css'
let idPost = require('../models/models.data');
const dataPost = require('../models/models.data');

function AddPost(){

    const [content, setContent] = useState();
    const nav = useNavigate();


    const addPost =()=>{
        //const tempData = [... data];
        //tempData.push({"id":idPost,"content":content,"comment":[]})
        //dataPost.push({"id":idPost,"content":content,"comment":[]})
        //setId(id+1)
        dataPost.push({"id":idPost,"content":content,"comment":[]})
        console.log(idPost);
        idPost = idPost+1;
        //setData(tempData)
        nav('/')
    }

    return(
        <>
            <div class="a">
            <h1>Thêm bài viết</h1>
            </div>

            <form action="addPost" method="post">
            <textarea onChange={evt=>setContent(evt.target.value)} className="text" rows = "5" cols = "80" name="content" id="content" type="text" placeholder="Nhập nội dung bài viết"></textarea>
            <button className="button" type="submit" onClick={addPost()}>Thêm bài viết</button>
            </form>
        </>
    )
}

export default AddPost;
