import React, { useEffect, useState } from "react";
import './Home.css';

function Home(){

    const [data,setData] = useState([{"id":0,"content":"abc","comment":["abc", "bcd"]}]);
    const [content, setContent] = useState();
    const [id, setId] = useState(0);

    const addPost =()=>{
        const tempData = [... data];
        tempData.push({"id":id,"content":content,"comment":[]});
        setId(id+1);
        setData(tempData);
    }

    const deletePost =(index)=>{
        const tempData = [... data];
        tempData.splice(index)
        setData(tempData)
    }

    return (
        <>
            <div className="a">
            <h1>Danh sách các bài viết</h1>
            </div>

            <div className="bottom">
            <label>Thêm bài viết mới:</label>
            </div>
            <div>
            <textarea onChange={evt=>setContent(evt.target.value)} class="text" rows = "5" cols = "80" name="comment" id="comment" type="text" placeholder="Nhập nội dung bình luận"></textarea>
            <input className="marginButton" value="Thêm" type="button" onClick={addPost}/>
            </div>

            {data.map((d,index) =>{
                return(
                    <div className="borderContent">
                        <p className="content">{d.content}{d.id}</p>

                        <div className="borderComment">
                            <label>Bình luận</label>
                            {d.comment.map(comment=>{
                                return (<p className="comment">{comment}</p>)
                            })}
                        </div>

                        <div className="button">
                            <input className="marginButton" value="Xóa bài viết" type="button" onClick={deletePost(index)}/>
                            <input className="marginButton" value="Sửa bài viết" type="button" onclick="updatePost({{this.id}})"/>
                            <input className="marginButton" value="Thêm bình luận" type="button" onclick="addComment({{this.id}})"/>
                        </div>
                    </div>
                )
            })}
        </>
    )
}

export default Home;
