import React, { useEffect, useState } from "react";
import './Home.css';
//const {dataPost} = require("../models/models.data");
//let {idPost} = require("../models/models.data");

function Home(){
    const [data,setData] = useState([]);
    const [content, setContent] = useState(localStorage.getItem("data"));
    const [comment, setComment] = useState();
    //const [id, setId] = useState(0);

    const addPost =()=>{
        const tempData = [... data];
        let idPost = parseInt(localStorage.getItem("idPost"))
        tempData.push({"id":idPost,"content":content,"comment":[]})
        let dataPost = localStorage.setItem("data", tempData)
        localStorage.setItem("idPost", idPost+1)
        //dataPost.push({"id":idPost,"content":content,"comment":[]})
        console.log(dataPost)
        //setId(id+1)
        idPost = idPost+1;
        setData(tempData)
    }

    const deletePost =(index)=>{
        const tempData = [... data];
        tempData.splice(index,1)
        dataPost.splice(index,1)
        setData(tempData)
    }

    const addComment =(index)=>{
        const tempData = [... data];
        tempData[index].comment.push(comment)
        dataPost[index].comment.push(comment)
        setData(tempData)
    }

    const updatePost =(index)=>{
        const tempData = [... data];
        tempData[index].content=content
        dataPost[index].content=content
        setData(tempData)
    }

    return (
        <>
            <div className="a">
            <h1>Danh sách các bài viết</h1>
            </div>

            <div className="bottom">
            <label>Thêm bài viết mới:</label>
            </div>
            <div>
            <textarea onChange={evt=>setContent(evt.target.value)} rows = "5" cols = "80" name="content" id="content" type="text" placeholder="Nhập nội dung bài viết"></textarea>
            <input className="marginButton" value="Thêm" type="button" onClick={addPost}/>
            </div>

            {data.map((d,index) =>{
                return(
                    <div className="borderContent">
                        <p className="content">{d.content}</p>

                        <div className="borderComment">
                            <label>Bình luận</label>
                            {d.comment.map(comment=>{
                                return (<p className="comment">{comment}</p>)
                            })}
                        </div>

                        <div className="button">
                            <input className="marginButton" value="Xóa bài viết" type="button" onClick={()=>deletePost(index)}/>
                            <textarea onChange={evt=>setContent(evt.target.value)} rows = "5" cols = "80" name="update" id="update" type="text" placeholder="Nhập nội dung cần sửa"></textarea>
                            <input className="marginButton" value="Sửa bài viết" type="button" onClick={()=>updatePost(index)}/>
                            <textarea onChange={evt=>setComment(evt.target.value)} rows = "5" cols = "80" name="comment" id="comment" type="text" placeholder="Nhập nội dung bình luận"></textarea>
                            <input className="marginButton" value="Thêm bình luận" type="button" onClick={()=>addComment(index)}/>
                        </div>
                    </div>
                )
            })}
        </>
    )
}

export default Home;
